markerClick.customFunction:
var params = widgetContext.stateController.getStateParams();
var moduleSelected = params['moduleSelected'];

if (moduleSelected && moduleSelected.entityId.id === entityId.id) {
    params['moduleSelected'] = null;
} else {
    params['moduleSelected'] = { entityId: entityId, entityName: entityName, entityLabel: entityLabel };
}

widgetContext.stateController.updateState(null, params);

tooltipAction.customFunction:
var params = JSON.parse(JSON.stringify(widgetContext.stateController.getStateParams()));
params['moduleSelected'] = {
        entityId: entityId,
        entityName: entityName,
        entityLabel: entityLabel,
};
params['targetEntityParamName'] = 'moduleSelected';
params['deviceSelected'] = null;

widgetContext.stateController.openState('module_details', params);

settings.cellContentFunction:
value = JSON.parse(value);
var content = ""
for(var i = 0; i < value.length; i++){
    content = content += "<span>-"+value[i]+"</span></br>";
}

return "<div>" + content + "</div>"

var state = value;

var stateSvg = getStateSvg(state);
var encodedStateSvg = encodeURIComponent(stateSvg);
var stateSvgUrl = 'data:image/svg+xml,' + encodedStateSvg;
var stateLabel = getStateLabel(state);

return '<span>'+
           '<img style="vertical-align: middle;" class="mat-icon" src="'+ stateSvgUrl +'">' +
           '<span style="vertical-align: middle; padding-left: 8px;">'+ stateLabel +'</span>' +
       '</span>';

function getStateSvg(state) {
    switch (state) {
        case 'alarmed':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#E64A19"/>' +
                   '</svg>';
        case 'normal':            
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#1F8B4D"/>' +
                   '</svg>';
        default:
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#AAAAAA"/>' +
                   '</svg>';
    }
}

function getStateLabel(state) {
    switch(state) {
        case 'alarmed':
            return 'Alarmado';
        case 'normal':
            return 'Normal';
        default:
            return state;
    }
}

var typeSensor = getTypeSensor(value);

return typeSensor;

function getTypeSensor(value){

    if(value !== entity['entityLabel']){
        return entity['entityLabel'];
    }

    var type = value;

    if(/S1/.test(value) || /S4/.test(value)){
        var nameSplit = value.split("_");
        type = 'Sensor ' + nameSplit[2];
    }

    return type;
}

var newValue = '';
switch (value) {
    case 'TemperaturaHumedad':
        newValue = 'Temperatura y humedad';
        break;
    case 'FlujoAire':
        newValue = 'Flujo de aire';
        break;
    default:
        newValue = value;
}

return newValue;

var state = value;

var stateSvg = getStateSvg(state);
var encodedStateSvg = encodeURIComponent(stateSvg);
var stateSvgUrl = 'data:image/svg+xml,' + encodedStateSvg;
var stateLabel = getStateLabel(state);

return '<span>'+
           '<img style="vertical-align: middle;" class="mat-icon" src="'+ stateSvgUrl +'">' +
           '<span style="vertical-align: middle; padding-left: 8px;">'+ stateLabel +'</span>' +
       '</span>';

function getStateSvg(state) {
    switch (state) {
        case 'alarmed':
            return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
                   '<circle cx="10" cy="10" r="6" fill="#E64A19"/>' +
                   '</svg>';
        case 'normal':            
