settings.tooltipFunction:
var name = (data['Etiqueta'] !== "") ? data['Etiqueta'] : defineName(data['entityLabel']);
var content = `<div style="font-family: Roboto;">
                 <div style="font-size: 14px; font-weight: 500; padding-bottom: 8px;">${name}</div>`;
                 
var deviceType = data['Tipo'];
if (typeof deviceType !== undefined) {
    content += '<div style="font-size: 12px; font-weight: normal;">' + getInfoByDeviceType(deviceType) + '</div>';
}
content += '</div>';
return content;

function getInfoByDeviceType(type) {
    switch (type) {
        case 'TemperaturaHumedad':
            //return `Temperatura: ${data['S1_TH_001_temperature']} °C </br> Humedad: ${data['S1_TH_001_humidity']} %HR`;
            return `Temperatura: ${data['S1_TH_001_temperature']} °C`;
        case 'Neteco_TemperaturaHumedad':
            return `Temperatura: ${data['current_temperature']} °C </br> Humedad: ${data['current_humidity']} %HR`;
        case 'Neteco_Hydrogen':
            return `Concentración de hidrógeno: ${data['hydrogen_concentration']}`;
        case 'Puerta':
            var estado = (data['S4_Puerta_001_unlocked'] === 'false') ? 'cerrada' : 'abierta';
            return `Puerta ${estado}`;
        case 'Liquidos':
            var estado = (data['S4_Liquidos_001_uninstalled'] === 'false') ? 'Con' : 'Sin';
            return `${estado} presencia de líquidos`;
        case 'FlujoAire':
            var estado = (data['S4_FlujoAire_001_uninstalled'] === 'false') ? 'Sin' : 'Con';
            return `${estado} presencia de aire`;
        case 'Netcool':
            return `Aire de retorno: ${data['return_air']} </br> %RH: ${data['%_rh']}`;
            break;
        case 'Chiller':
            var estado = (data["ajuste_on_off"] === 1) ? "On" : "Off";
            return `Estado: ${estado}`;
            break;
        case 'UPS':
            return `V1: ${data['voltaje_1']}, V2: ${data['voltaje_2']}, V3: ${data['voltaje_3']} <br> C1: ${data['corriente_1']}, C2: ${data['corriente_2']}, C3: ${data['corriente_3']}`;
            break;
        case 'Planta':
            return `V1: ${data['voltaje_1']}, V2: ${data['voltaje_2']}, V3: ${data['voltaje_3']} <br> C1: ${data['corriente_1']}, C2: ${data['corriente_2']}, C3: ${data['corriente_3']}`;
            break;
        default:
            return 'TODO';
    }
}

function defineName(name){
    var newName = name;
    if(/S1/.test(name) || /S4/.test(name)){
        var split = name.split("_");
        newName = `Sensor ${split[2]}`;
    }
    
    return newName;
}

settings.markerImageFunction:
var type = data['Tipo'];

var deviceSelected = data['deviceSelected'] !== undefined;
var modeSelected = dsData.filter(function(data){return data['deviceSelected'] !== undefined; }).length > 0;

var svgType = defineSvg(type);
var width = defineWidth(type);

if(deviceSelected){
    width += 5;
}

return {
    url: 'data:image/svg+xml,' + encodeURIComponent(svgType),
    size: width
}

function defineWidth(type){
    var width = 0;

    switch(type){
        case 'Puerta':
            width = 30;
            break;
        case 'UPS':
            width = 35;
            break;
        case 'Planta':
            width = 35;
            break;
        case 'Chiller':
            width = 35;
            break;
        default:
            width = 25;
    }

    return width;
}

