markerClick.customFunction:
var params = JSON.parse(JSON.stringify(widgetContext.stateController.getStateParams()));
var deviceSelected = params['deviceSelected'];
if (deviceSelected && deviceSelected.entityId.id === entityId.id) {
    params['deviceSelected'] = null;
} else {
    params['deviceSelected'] = { entityId: entityId };
}
widgetContext.stateController.updateState(null, params);

settings.markdownTextFunction:
var type, sensor;

if(data && data.length){
    type = defineType(data[0]['Tipo']);
    sensor = defineSensor(data[0]['entityName']);
}else{
    name = 'N/A';
    sensor = 'N/A';
}

var srcImage = defineImage(data[0]['Tipo']);

var header = `
    <div class="flex items-stretch gap-2" style="padding: 16px;">
        <div class="flex flex-col justify-center">
            <button id="go-back" title="Go back" type="button" style="background: none; border: none; cursor: pointer;">
              <img src="https://fonts.gstatic.com/s/i/materialicons/arrow_back/v6/24px.svg" style="height: 24px;" />
            </button>
          </div>
        <div class="flex flex-col justify-center">
            <img style="vertical-align: middle;" class="title-icon mat-icon" src="${srcImage}">
        </div>
        <div class="flex-1 flex flex-col justify-center">
            <div style="font-size: 15px; font-weight: bold; flex: 1;">${type}</div>
            <div style="font-size: 12px; flex: 1;">${sensor}</div>
        </div>
    </div>
`;

return `
    <div class="main-layout" style="width: 100%; height: 100%;" fxLayout="column">
        ${header}
    </div>
`;

function defineType(type){
    var newType = '';

    switch (type) {
        case 'TemperaturaHumedad':
            newType = 'Temperatura y humedad';
            break;
        case 'FlujoAire':
            newType = 'Flujo de aire';
