var dispositivo = JSON.parse(metadata.dispositivo);
var umbrales = (dispositivo.umbrales !== undefined) ? dispositivo.umbrales : JSON.parse(metadata.umbrales)[dispositivo.type][dispositivo.dispositivo];

var temperaturaSuperior = umbrales.temperature.superior;
var temperaturaInferior = umbrales.temperature.inferior;
var humedadSuperior = umbrales.humidity.superior;
var humedadInferior = umbrales.humidity.inferior;

var fueraUmbral = {
    "temperature": false,
    "humidity": false
};

var nivelAlarma = {
    "temperature": "",
    "humidity": ""
};

if (dispositivo.temperature < temperaturaInferior){
    fueraUmbral.temperature = "inferior";
}else if(dispositivo.temperature > temperaturaSuperior){
    fueraUmbral.temperature = "superior";
}

if(fueraUmbral.temperature !== false){
    var nivelesAlarma = umbrales.temperature.levelsAlarm;

    if(typeof nivelesAlarma === "object"){
        for (var i = 0; i < nivelesAlarma.length; i++) {
            var nivel = nivelesAlarma[i];
            if (dispositivo.temperature > nivel.min && dispositivo.temperature <= nivel.max) {
                nivelAlarma.temperature = nivel.level;
                break;
            }
        }
    }else{
        nivelAlarma.temperature = nivelesAlarma;
    }

}

if (dispositivo.humidity < humedadInferior) {
    fueraUmbral.humidity = "inferior";
}else if(dispositivo.humidity > humedadSuperior){
    fueraUmbral.humidity = "superior";
}

if(fueraUmbral.humidity !== false){
    var nivelesAlarma = umbrales.humidity.levelsAlarm;

    if(typeof nivelesAlarma === "object"){
        for (var i = 0; i < nivelesAlarma.length; i++) {
            var nivel = nivelesAlarma[i];
            if (dispositivo.humidity > nivel.min && dispositivo.humidity <= nivel.max) {
                nivelAlarma.humidity = nivel.level;
                break;
            }
        }
    }else{
        nivelAlarma.humidity = nivelesAlarma;
    }

}

delete umbrales.temperature.levelsAlarm;
delete umbrales.humidity.levelsAlarm;
dispositivo.umbrales = umbrales;

metadata.dispositivo = JSON.stringify(dispositivo);
metadata.nivelAlarma = JSON.stringify(nivelAlarma);
metadata.fueraUmbral = JSON.stringify(fueraUmbral);

delete metadata.umbrales;

return {msg: msg, metadata: metadata, msgType: msgType};

